mapper.select:
SELECT id,name,title,sub_title AS subTitle,`describe`,image,price,status,thumb
FROM
goods WHERE id = #{id}

mapper.update:
UPDATE goods
title = #{productRequest.title}, sub_title = #{productRequest.subTitle}, image = #{productRequest.images}, describe=#{productRequest.describe}, name=#{productRequest.productName}
where id = #{productRequest.id}
