mapper.select:
select id as id, name as name, open_id as openId, phone as phone, sex as sex,address as address,invite_code as inviteCode
FROM member
WHERE open_id = #{openId}

select id as id, name as name, open_id as openId, phone as phone, sex as sex,address as address,invite_code as inviteCode
FROM member
WHERE invite_code = #{inviteCode}

select id as id, name as name, open_id as openId, phone as phone, sex as sex,address as address,invite_code as inviteCode
FROM member
WHERE id = #{id}

mapper.update:
UPDATE members

    name = #{member.name},
    phone = #{member.phone}

where id = #{member.id}

mapper.select:
select id as id, name as name, open_id as openId, phone as phone, sex as sex,address as address,invite_code as inviteCode
FROM member
WHERE name = #{name}
