mapper.select:
SELECT id,order_no AS orderNo,goods_id AS goodsId,buy_member_id AS buyMemberId,goods_spec_id AS goodsSpecId,goods_num as goodsNum,address_id AS addressId,batch_id AS batchId,goods_code AS goodsCode,tax_info AS taxInfo,buyer_message AS buyerMessage, status,created_time AS createdTime,type,address,person,phone,send_time AS  sendTime
FROM hsll_order
WHERE order_no = #{orderNo}

mapper.update:
UPDATE hsll_order
SET batch_id = #{order.batchId},goods_code = #{order.goodsCode}, person = #{order.person}, address = #{order.address}, phone = #{order.phone}
WHERE id = #{order.id}

mapper.select:
SELECT id,order_no AS orderNo,goods_id AS goodsId,buy_member_id AS buyMemberId,goods_spec_id AS goodsSpecId,goods_num as goodsNum,address_id AS addressId,batch_id AS batchId,goods_code AS goodsCode,tax_info AS taxInfo,buyer_message AS buyerMessage, status,created_time AS createdTime,type,address,person,phone,send_time AS  sendTime
FROM hsll_order
WHERE buy_member_id LIKE #{memberId}
limit #{offset},#{pageSize}

SELECT a.id,a.order_no AS orderNo,a.goods_id AS goodsId,a.buy_member_id AS buyMemberId,a.goods_spec_id AS goodsSpecId,a.goods_num as goodsNum,a.address_id AS addressId,a.batch_id AS batchId,a.goods_code AS goodsCode,a.tax_info AS taxInfo,a.buyer_message AS buyerMessage, a.status,a.created_time AS createdTime,a.type,a.address,a.person,a.phone,a.send_time AS sendTime, b.name as goodsName,c.spec_name as goodsSpecName,d.name as buyMemberName FROM hsll_order as a left join goods as b on a.goods_id=b.id left join goods_spec as c on a.goods_spec_id=c.id left join member d on a.buy_member_id=d.id where 1=1
and status=#{status}
limit #{startPage},#{pageSize}

SELECT count(1)
FROM hsll_order

    WHERE status=#{status}

mapper.update:
UPDATE hsll_order
SET status=${status}
WHERE order_no = #{orderNo}

mapper.select:
SELECT id,order_no AS orderNo,goods_id AS goodsId,buy_member_id AS buyMemberId,goods_spec_id AS goodsSpecId,goods_num as goodsNum,address_id AS addressId,batch_id AS batchId,goods_code AS goodsCode,tax_info AS taxInfo,buyer_message AS buyerMessage, status,created_time AS createdTime,type,address,person,phone,send_time AS  sendTime,
b.name as goodsName,c.spec_name as goodsSpecName,d.name as buyMemberName
FROM hsll_order as  a
left join goods  as b on  a.goods_id=b.id
left join goods_spec as c on a.goods_spec_id=c.id
left join member d on a.buy_member_id=d.id
where 1=1

     and  b.name like #{searchName} or a.order_no like #{searchName}
    or a.goods_code like #{searchName}

limit #{pageNo},#{pageSize}
